package at.fhv.itm14.fhvgis.persistence.hibernate.objects;

import java.util.UUID;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.Id;
import javax.persistence.JoinColumn;
import javax.persistence.ManyToOne;
import javax.persistence.Table;

import com.vividsolutions.jts.geom.GeometryCollection;

/**
 * Geofence generated by hbm2java
 */
@Entity
@Table(name = "geofence", schema = "public")
public class Geofence implements java.io.Serializable {

	private UUID id;
	private Poi poi;
	private GeometryCollection positions;

	public Geofence() {
	}

	public Geofence(UUID id, Poi poi, GeometryCollection positions) {
		this.id = id;
		this.poi = poi;
		this.positions = positions;
	}

	@Id

	@Column(name = "id", unique = true, nullable = false)
	public UUID getId() {
		return this.id;
	}

	public void setId(UUID id) {
		this.id = id;
	}

	@ManyToOne(fetch = FetchType.LAZY)
	@JoinColumn(name = "poi_id", nullable = false)
	public Poi getPoi() {
		return this.poi;
	}

	public void setPoi(Poi poi) {
		this.poi = poi;
	}

	@Column(name = "positions", nullable = false)
	public GeometryCollection getPositions() {
		return this.positions;
	}

	public void setPositions(GeometryCollection positions) {
		this.positions = positions;
	}

}
